-- Name: res_device; Type: VIEW; Schema: public; Owner: odoo
--

CREATE VIEW public.res_device AS
 SELECT d.id,
    d.user_id,
    d.create_uid,
    d.write_uid,
    d.session_identifier,
    d.platform,
    d.browser,
    d.ip_address,
    d.country,
    d.city,
    d.device_type,
    d.revoked,
    d.first_activity,
    d.last_activity,
    d.create_date,
    d.write_date
   FROM public.res_device_log d
  WHERE ((NOT (EXISTS ( SELECT 1
           FROM public.res_device_log d2
          WHERE ((d2.user_id = d.user_id) AND ((d2.session_identifier)::text = (d.session_identifier)::text) AND (NOT ((d2.platform)::text IS DISTINCT FROM (d.platform)::text)) AND (NOT ((d2.browser)::text IS DISTINCT FROM (d.browser)::text)) AND ((d2.last_activity > d.last_activity) OR ((d2.last_activity = d.last_activity) AND (d2.id > d.id))) AND (d2.revoked = false))))) AND (d.revoked = false));


